{% set ns = namespace(sum=0) %}
{% set total = tests|count %}
{% set r = 16 %}
{% set spacing = 0.5 %}
{% set C = 2 * 3.14159 * r %}
{% for category, sub_tests in tests|groupby('status.category') %}
  {% set count = sub_tests|count %}
  {% set L = count / total * C %}
  {% set color = colors[category] %}


  {% set ns.sum = ns.sum + L %}
{% endfor %}
{{ total }}
